/* eslint-disable prettier/prettier */
<template>
<div class="container">
    <transition-group name="fade" tag="div">
      <div v-for="i in [currentIndex]" :key="i">
        <img :src="currentImg" />
      </div>
    </transition-group>
    <a class="prev" @click="prev" href="#">&#10094; Previous</a>
    <a class="next" @click="next" href="#">&#10095; Next</a>
  </div>
</template>

<script  lang="ts">
export default {
name:"MySlider",
 data() {
    return {
      images: [
         "https://www.androidauthority.com/wp-content/uploads/2018/08/Walli-best-photography-background-apps-for-android.jpg",
        "https://www.cityzeum.com/images/wikimedia/old_city_hammamet.jpg",
         "https://wallpaperaccess.com/full/1209710.jpg",
         "data:image/jpeg;base64,",
        "https://cdn.pixabay.com/photo/2016/12/04/19/30/berlin-cathedral-1882397_1280.jpg"
      ],
      timer: null,
      currentIndex: 0
    };
  },
   mounted: function() {
    this.startSlide();
  },
 methods: {
    startSlide: function() {
     // this.timer = setInterval(this.next, 4000);
    },

    next: function() {
      this.currentIndex += 1;
    },
    prev: function() {
      this.currentIndex -= 1;
    }
  },
  computed: {
    currentImg: function() {
      return this.images[Math.abs(this.currentIndex) % this.images.length];
    }
  }


};

</script>

<style scoped>
.container{
    width:90vw;
    height:90vh;
    margin-top:75px;
    margin-left:120px;

}
.fade-enter-active,
.fade-leave-active {
  transition: all 0.9s ease-out;
  overflow-y: hidden;
  visibility: visible;
  position: absolute;
  width:90vw;
  opacity: 1;
 
}

.fade-enter,
.fade-leave-to {
  visibility: hidden;
  position: absolute;
  width:90vw;
  opacity: 0;
}
.prev, .next {
  cursor: pointer;
  position: absolute;
  display:flex;
  top: 35%;
  width: 110px;
  height:250px;
  padding-top: 25px;
  align-items:center;
  justify-content:center;
   color: #c12700 ;
  font-weight: bold;
  font-size: 18px;
  transition: 0.7s ease;
  border-radius: 0 4px 4px 0;
  text-decoration: none;
  user-select: none;
}
img {
  height:600px;
 
 width:100%
}
.next {
  right: 5px;
}
.prev {
  left: 5px;
}

.prev:hover, .next:hover {
  background-color: #C1FE84;
}

</style>